%%configure -f
{
    "conf":{
        "spark.rpc.message.maxSize":"2000"
    }

if not is_data_source_gzipped:
    for file_id in files_to_download:
        time_delta = datetime.datetime.now() - now
        #check if jwt is not expired (renew every 20 mins)
        if time_delta.total_seconds() < 1200:
            api_url = "https://data-broker.service.deutschebahn.com/api/files-api/v1/files/" + file_id
            headers = {"Authorization": "Bearer " + access_token}
            file = requests.get(api_url, headers=headers)
            adls_path = 'abfss://%s@%s.dfs.core.windows.net/%s/%s' % (container_name, account_name, folder_structure, file_id) #zukünftig noch metadaten auslesen
            #chunk file in 500Mb pieces
            for j in file.iter_content(500000000):
                mssparkutils.fs.append(adls_path, str(j, 'utf-8'), True)
        else:
            # renew jwt token
            payload = {'client_id': databrokerClientId,'scope':'datasources', 'client_secret':databrokerSecret,'grant_type': 'client_credentials'}
            api_url = "https://auth.businesshub.deutschebahn.com/auth/realms/data-broker/protocol/openid-connect/token"
            result = requests.post(api_url, data=payload)
            access_token=result.json()['access_token']
            now = datetime.datetime.now()

import gzip

if is_data_source_gzipped:
    for file_id in files_to_download:
        time_delta = datetime.datetime.now() - now
        #check if jwt is not expired (renew every 20 mins)
        if time_delta.total_seconds() < 1200:
            api_url = "https://data-broker.service.deutschebahn.com/api/files-api/v1/files/" + file_id
            headers = {"Authorization": "Bearer " + access_token}
            file = requests.get(api_url, headers=headers)
            adls_path = 'abfss://%s@%s.dfs.core.windows.net/%s/%s' % (container_name, account_name, folder_structure, file_id) #zukünftig noch metadaten auslesen
            data = gzip.decompress(file.content).decode('utf-8')
            #chunk file in pieces
            for i in range(0, len(data), 100000000):
                mssparkutils.fs.append(adls_path, data[i:i+100000000], True)
        else:
            # renew jwt token
            payload = {'client_id': databrokerClientId,'scope':'datasources', 'client_secret':databrokerSecret,'grant_type': 'client_credentials'}
            api_url = "https://auth.businesshub.deutschebahn.com/auth/realms/data-broker/protocol/openid-connect/token"
            result = requests.post(api_url, data=payload)
            access_token=result.json()['access_token']
            now = datetime.datetime.now()
